import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import {Storage} from '@ionic/storage';

export interface Product{
  id: number;
  name: string;
  price: number;
  amount: number;
  imgUrl: string;
}
@Injectable({
  providedIn: 'root'
})
export class CartService {
  data: Product[]=[
    {id: 0, name: 'Chocolate', price: 50, amount:1, imgUrl:'data:image/jpeg;base64,'},
    {id: 1, name: 'Flower', price: 100, amount:1, imgUrl:'data:image/jpeg;base64,'},
    {id: 2, name: 'Bracelet', price: 30, amount:1, imgUrl:'data:image/jpeg;base64,'},
    {id: 3, name: 'Ring', price: 2000, amount:1, imgUrl:'data:image/jpeg;base64,'}
  ];
  localData=[];
  cart =[];
  
  private cartItemCount = new BehaviorSubject(0);
  constructor(private storage:Storage) { }
  
  setData(id, data){
    this.data[id] = data;
  }

  getData(id){
    this.data[id];
  }

  getProduct(){
    this.saveProduct();
    return this.localData=JSON.parse(localStorage.getItem('product'))
  }
  
  saveProduct(){
    if(this.data ===[]){return;}
    localStorage.setItem('product', JSON.stringify(this.data));
  }

  getCart(){
    this.saveCart();
    return this.cart = JSON.parse(localStorage.getItem('cart'))
  }
  
  saveCart(){
    if(this.cart ===[]){return;}
    localStorage.setItem('cart', JSON.stringify(this.cart));
  }
  getCartItemCount(){
   return this.cartItemCount;
  }

  addProduct(product){
    let added = false;
    for (let p of this.cart) {
      if (p.id===product.id) {
        p.amount++;
        added = true;
        localStorage.setItem('cart', JSON.stringify(this.cart));
        break;
      }
    }if (!added) {
      this.cart.push(product);
      this.saveCart();
    } 
    this.cartItemCount.next(this.cartItemCount.value + 1);
    localStorage.setItem('cart', JSON.stringify(this.cart));
  }
  decreaseProduct(product){
    for (let [index,p] of this.cart.entries()) {
      if(p.id === product.id){
        p.amount -= 1;
        localStorage.setItem('cart', JSON.stringify(this.cart));
        if (p.amount == 0) {
          this.cart.splice(index,1);
          localStorage.setItem('cart', JSON.stringify(this.cart));
        }
      }
    }
    this.cartItemCount.next(this.cartItemCount.value - 1);
    localStorage.setItem('cart', JSON.stringify(this.cart));
  }
  removeProduct(product) {
    for (let [index, p] of this.cart.entries()) {
      if (p.id === product.id) {
        this.cartItemCount.next(this.cartItemCount.value - p.amount);
        p.amount=p.amount-p.amount+1;
        
        this.cart.splice(index, 1);
        localStorage.setItem('cart', JSON.stringify(this.cart));
        // console.log(this.cart);
        
      }
  }
}
}
